import React,{useContext,useEffect,useState} from 'react';
import '../index.css';
import LoginHistory from './LoginHistory';
import UserDetails from './UserDetails';
import {useParams} from 'react-router-dom';
import { PeopleContext } from '../contexts/PeopleContextProvider';
import {Avatar} from 'evergreen-ui';
import Moment from 'react-moment';


const UserInfo = () => {
    const {_id} = useParams();
    const {data} = useContext(PeopleContext);
    const user = data.find(person=>person.id === Number(_id));
    let logins = user.logins;
    const days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
    const months = ['JAN', 'FEB', 'MAR', 'APR', 'JUN', 'JUL', 'AUG','SEP','OCT','NOV','DEC'];
    const [stats,setStats] = useState([]);
    useEffect(()=>{
        let obj={
            byYear:{},
            byWeek:{}
          }
          logins.forEach(login=>{
            const date = new Date(login.date)
            const day = days[date.getDay()] // Mon or Tue etc
            obj.byWeek[day] ? obj.byWeek[day]++ : obj.byWeek[day]=1
            const month = months[date.getMonth()];
            obj.byYear[month] ? obj.byYear[month]++ : obj.byYear[month] = 1
          })
          setStats(obj)
    },[logins,days,months])
    console.log(stats,'jj')


    return(
        <div className="user-info-component">
           <div id="contact-details" key={user.id}>
                <div className="personal-info">
                <Avatar name={`${user.first_name} ${user.last_name}`} size={200} />
                <h1 className="user-name">{`${user.first_name} ${user.last_name}`}</h1>
                </div>
                <p><span className="prop">City:</span>{user.city}</p>
                <p><span className="prop">State:</span>{user.state}</p>
                <p><span className="prop">Safe email:</span> {user.safe_email}</p>
                <p><span className="prop">Email:</span> {user.email}</p>
                <p><span className="prop">Last activity:</span> <Moment date={user.logins[user.logins.length-1].date} durationFromNow/> ago</p>
                <LoginHistory user = {user} />
                <UserDetails stats={stats} />
           </div>
        </div>
    )
}
export default UserInfo;
