import React,{useContext, useState,useEffect} from 'react';
import '../index.css';
import LoginHistory from './LoginHistory';
import {useParams} from 'react-router-dom';
import { PeopleContext } from '../contexts/PeopleContextProvider';
import PersonalInfo from './PersonalInfo';
import UserDetails from './UserDetails';
import {Spinner} from 'evergreen-ui';
const Accaunt = () => {
    const {data} = useContext(PeopleContext);
    const {_id} = useParams();
    const user = data.find(person=>person.id === Number(_id));
    let logins = user.logins;
    const [year,setYear] = useState();
    const [week,setWeeks] = useState();

      useEffect(()=>{
      const days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
      const months = ['Jan', 'Feb', 'Mar', 'Apr', 'Jun', 'Jul', 'Aug','Sep','Oct','Nov','Dec'];
        let obj={
            byMonth:{},
            byWeek:{}
          }
          logins.forEach(login=>{
            const date = new Date(login.date)
            const day = days[date.getDay()] // Mon or Tue etc
            obj.byWeek[day] ? obj.byWeek[day]++ : obj.byWeek[day]=1
            const month = months[date.getMonth()];
            obj.byMonth[month] ? obj.byMonth[month]++ : obj.byMonth[month] = 1
          })
          setYear(obj.byMonth);
          setWeeks(obj.byWeek);
    },[logins]);
    console.log(year.Jan)

    const userDetails = (year && week !== undefined) ? <UserDetails year={year} week={week}/> : <Spinner />

    return(
        <div className="user-info-component">
           <div id="contact-details">
           <PersonalInfo user = {user} />
           <LoginHistory user = {user} />
           {userDetails}
           </div>
        </div>
    )
}
export default Accaunt;
