import React,{useContext} from 'react';
import '../index.css';
import {useParams} from 'react-router-dom';
import { PeopleContext } from '../contexts/PeopleContextProvider';
import {Table} from 'evergreen-ui';
import Moment from 'react-moment';

const LoginHistory = () => {
    const {_id} = useParams();
    const {data} = useContext(PeopleContext);
    const user = data.find(person=>person.id == _id);
    const loginHistory = user.logins.length ? (user.logins.map(item=>{
        const dateToFormat = item.date;
        return(
            <Table.Row key={user.id}>

                <Table.TextCell><Moment>{dateToFormat}</Moment></Table.TextCell>

            </Table.Row>
        )
    })) : ('Data is not available')
    return(
        <div id="login-history">
            <Table>
                <Table.Head padding={10}>
                    <strong>Login History</strong>
                </Table.Head>
                <Table.Body height={200}>
                    {loginHistory}
                </Table.Body>
            </Table>
        </div>
    )
}
export default LoginHistory;
