import React,{useContext, useState, useEffect} from 'react';
import {PeopleContext} from '../contexts/PeopleContextProvider';
import {Link} from 'react-router-dom';
import '../index.css';
import {Table,SearchInput} from 'evergreen-ui'


const TableComponent = () => {
    const {data} = useContext(PeopleContext);
    const [searchTerm, setSearchTerm] = useState("");
    const [searchResults, setSearchResults] = useState([]);

    const handleChange = event => {setSearchTerm(event.target.value)};

    useEffect(() => {
        const results = data.filter(person => person.first_name.toLowerCase().includes(searchTerm));
        setSearchResults(results);
    }, [data, searchTerm]);

        return(
            <div className="table-component">
                <div className="table-wrapper">
                    <Table elevation={2} border='muted'>
                        <SearchInput placeholder="filter name ..." onChange={handleChange} value={searchTerm} width="100%" height={40} marginY={10}/>
                        <Table.Head>
                            <Table.TextHeaderCell>
                                First Name
                            </Table.TextHeaderCell>
                            <Table.TextHeaderCell>
                                Email
                            </Table.TextHeaderCell>
                            <Table.TextHeaderCell>
                                City
                            </Table.TextHeaderCell>
                            <Table.TextHeaderCell>
                                State
                            </Table.TextHeaderCell>
                        </Table.Head>

                        <Table.Body height={700}>
                            {searchResults.map(person => {
                                const city = person.city !== null ? person.city : ' N/A';
                                const state = person.state !== null ? person.state : ' N/A';
                            return(
                                <Link to={'/' + person.id} key={person.id}>
                                <Table.Row isSelectable >
                                    <Table.TextCell>{`${person.first_name} ${person.last_name}`}</Table.TextCell>
                                    <Table.TextCell>{person.email}</Table.TextCell>
                                    <Table.TextCell>{city}</Table.TextCell>
                                    <Table.TextCell>{state}</Table.TextCell>
                                </Table.Row>
                            </Link>
                            )
                            })}
                        </Table.Body>

                    </Table>
                </div>
        </div>
        )
}
export default TableComponent;
