import React,{useContext} from 'react';
import '../index.css';
import {useParams} from 'react-router-dom';
import { PeopleContext } from '../contexts/PeopleContextProvider';
import {Avatar} from 'evergreen-ui';

const PeopleInfo = () => {
    const {_id} = useParams();
    const {data} = useContext(PeopleContext);
    const user = data.find(person=>person.id == _id);
    return(
        <div className="people-info-component">
           <div className="personal-info">
           <Avatar name={`${user.first_name} ${user.last_name}`} size={40} />
           </div>
        </div>
    )
}
export default PeopleInfo;
