import React,{useState,useEffect} from 'react';
import {
    VictoryBar,
    VictoryChart,
    VictoryLine,
    VictoryPie,
  } from "victory";

  const GlobalGraphs = ({data}) => {
      const [nums,setNums] = useState(null);
      useEffect(()=>{
          let obj = {};
          for(let i=0;i<data.length;i++){
            let state = data[i].state
            obj[state] ? obj[state]++ : obj[state] = 1;
          }
          setNums(obj)
      },[data])

      return(
        <div className="global-graphs">
            {nums}
        </div>
      )
  }
  export default GlobalGraphs;
