import React from 'react';
import {VictoryChart,VictoryLine,VictoryPie} from "victory";
import {Heading} from 'evergreen-ui';
const UserDetails = (props) => {
    const {stats} = props.stats
    return(
        <div className="graphs">
            <Heading size={800}>Monthly activity</Heading>
            <VictoryChart>
                <VictoryLine
                    data={[
                        { x: "Jan", y: props.stats.byMonth.JAN },
                        { x: "Feb", y: props.stats.byMonth.FEB },
                        { x: "Mar", y: props.stats.byMonth.MAR },
                        { x: "Apr", y: props.stats.byMonth.APR },
                        { x: "May", y: props.stats.byMonth.MAY },
                        { x: "Jun", y: props.stats.byMonth.JUN },
                        { x: "Jul", y: props.stats.byMonth.JUL },
                        { x: "Aug", y: props.stats.byMonth.AUG },
                        { x: "Sep", y: props.stats.byMonth.SEP },
                        { x: "Oct", y: props.stats.byMonth.OCT },
                        { x: "Nov", y: props.stats.byMonth.NOV },
                        { x: "Dec", y: props.stats.byMonth.DEC }
                        ]}
                />
            </VictoryChart>
            <Heading size={800}>Daily activity</Heading>
            <VictoryPie
                colorScale={["#084B8A", "#00783E", "#00783E", "#EFBB35","#234361","#008f68", "#6DB65B"]}
                data={[
                    { x: "Mon", y: stats.byWeek.MON },
                    { x: "Tue", y: stats.byWeek.MON },
                    { x: "Wed", y: stats.byWeek.MON },
                    { x: "Thu", y: stats.byWeek.MON },
                    { x: "Fri", y: stats.byWeek.MON },
                    { x: "Sat", y: stats.byWeek.MON },
                    { x: "Sun", y: stats.byWeek.MON }
                    ]}
                    />
        </div>
    )
}
export default UserDetails;
