import React,{useContext} from 'react';
import '../index.css';
import {useParams} from 'react-router-dom';
import { PeopleContext } from '../contexts/PeopleContextProvider';
import {Avatar,Button} from 'evergreen-ui';
import Moment from 'react-moment';
const PeopleInfo = () => {
    const {_id} = useParams();
    const {data} = useContext(PeopleContext);
    const user = data.find(person=>person.id == _id);
    const dateToFormat = user.logins[user.logins.length-1].date;
    return(
        <div className="people-info-component">
           <div className="personal-info">
           <Avatar name={`${user.first_name} ${user.last_name}`} size={140} />
           <h1 className="user-name">{`${user.first_name} ${user.last_name}`}</h1>
           </div>
           <div id="details">
               <p><span className="prop">City:</span>{user.city}</p>
               <p><span className="prop">State:</span> {user.state}</p>
               <p><span className="prop">Safe email:</span> {user.safe_email}</p>
               <p><span className="prop">Email:</span> {user.email}</p>
               <p><span className="prop">Last activity:</span> <Moment>{dateToFormat}</Moment></p>
               <Button appearance="primary">Hi Evergreen!</Button>
           </div>
        </div>
    )
}
export default PeopleInfo;
