import React,{useContext} from 'react';
import '../index.css';
import {useParams} from 'react-router-dom';
import { PeopleContext } from '../contexts/PeopleContextProvider';
import {Avatar} from 'evergreen-ui';

const PeopleInfo = () => {
    const {_id} = useParams();
    const {data} = useContext(PeopleContext);
    const user = data.find(person=>person.id == _id);
    return(
        <div className="people-info-component">
           <div className="personal-info">
           <Avatar name={`${user.first_name} ${user.last_name}`} size={40} />
           <h1>{`${user.first_name} ${user.last_name}`}</h1>
           </div>
           <div className="details">
               <p className='details'>{user.city}</p>
               <p className='details'>{user.state}</p>
               <p className='details'>{user.safe_email}</p>
               <p className='details'>{user.email}</p>
               <p className='details'>{user.logins[user.logins.length-1].date}</p>
           </div>
        </div>
    )
}
export default PeopleInfo;
