import React,{useContext,useEffect,useState} from 'react';
import {PeopleContext} from '../contexts/PeopleContextProvider';
import '../index.css';
import {
    VictoryBar,
    VictoryChart,
    VictoryLine,
    VictoryPie,
  } from "victory";

const GlobalGraphs = () => {
    const {data} = useContext(PeopleContext);
    const [states,setStates] = useState([]);
    useEffect(()=>{
        const obj = {};
        for(let i=0;i<data.length;i++){
            data[i].state===undefined ? obj[data[i]] = 1 : obj[data[i]]++
        }
        setStates([...states,{obj}])
    },[states, data])    
    return(
        <div className="global-graphs">

        </div>
    )
}
export default GlobalGraphs;
