import React,{useContext,useState} from 'react';
import {PeopleContext} from '../contexts/PeopleContextProvider';
import '../index.css';
import {Table} from 'evergreen-ui'
import {NavLink} from 'react-router-dom';

const TableComponent = () => {
const {data} = useContext(PeopleContext);
const [input, setInput] = useState(data);


    return(
        <div className="table-component">
            <Table>
                <Table.SearchHeaderCell  placeholder='Search by email...' height={50} onChange={(e)=>setInput(input.filter(person=>person.first_name === e.target.value))}/>
                <Table.Head>
                    <Table.TextHeaderCell>
                        First Name
                    </Table.TextHeaderCell>
                    <Table.TextHeaderCell>
                        Last Name
                    </Table.TextHeaderCell>
                    <Table.TextHeaderCell>
                        City
                    </Table.TextHeaderCell>
                    <Table.TextHeaderCell>
                        State
                    </Table.TextHeaderCell>
                </Table.Head>

                <Table.Body height={600}>
                    {input.map(person => (
                    <NavLink to={'/' + person.id} key={person.id}>
                        <Table.Row key={person.id}  isSelectable >
                            <Table.TextCell>{person.first_name}</Table.TextCell>
                            <Table.TextCell>{person.last_name}</Table.TextCell>
                            <Table.TextCell>{person.city}</Table.TextCell>
                            <Table.TextCell>{person.state}</Table.TextCell>
                        </Table.Row>
                    </NavLink>
                    ))}
                </Table.Body>
            </Table>
      </div>
    )
}
export default TableComponent;
