import React from 'react';
import {VictoryChart,VictoryLine,VictoryPie} from "victory";
import $ from 'jquery';
import {findDOMNode} from 'react-dom';
const UserDetails = ({user}) => {

    const handleToggle=()=>{
        const el = findDOMNode(this.ref.toggle);
        $(el).accordion()
    }
    return(
        <div className="graphs">
            <h1 id ='year' onClick={handleToggle}>Yearly activity</h1>
            <VictoryChart ref='toggle'>
                <VictoryLine
                    data={[
                        { x: "Jan", y: 5 },
                        { x: "Feb", y: 4 },
                        { x: "Mar", y: 3 },
                        { x: "Apr", y: 0 },
                        { x: "May", y: 1 },
                        { x: "Jun", y: 3 },
                        { x: "Jul", y: 5 },
                        { x: "Aug", y: 4 },
                        { x: "Sep", y: 0 },
                        { x: "Oct", y: 1 },
                        { x: "Nov", y: 7 },
                        { x: "Dec", y: 5 }
                        ]}
                />
            </VictoryChart>
            <h1>Weekday activity</h1>
            <VictoryPie ref = 'toggle'
                colorScale={["#084B8A", "#00783E", "#00783E", "#EFBB35","#234361","#008f68", "#6DB65B"]}
                data={[
                    { x: "Mon", y: 5 },
                    { x: "Tue", y: 4 },
                    { x: "Wed", y: 6 },
                    { x: "Thu", y: 4 },
                    { x: "Fri", y: 3 },
                    { x: "Sat", y: 2 },
                    { x: "Sun", y: 9 }
                    ]}
                    />
        </div>
    )
}
export default UserDetails;
