import React,{useContext} from 'react';
import '../index.css';
import {useParams} from 'react-router-dom';
import { PeopleContext } from '../contexts/PeopleContextProvider';

const PeopleInfo = () => {
    const {_id} = useParams();
    const {data} = useContext(PeopleContext);
    const userInfo = data.find(person=>person.id === _id);
    return(
        <div className="people-info-component">
           <h1>{_id}</h1>
        </div>
    )
}
export default PeopleInfo;
