import React,{useContext} from 'react';
import '../index.css';
import {useParams} from 'react-router-dom';
import { PeopleContext } from '../contexts/PeopleContextProvider';

const PeopleInfo = () => {
    const {_id} = useParams();
    const {data} = useContext(PeopleContext);
    const userInfo = data.find(person=>person.id === _id);
    return(
        <div className="people-info-component">
            <h1>{userInfo.first_name}</h1>
            <h1>{userInfo.last_name}</h1>
            <h1>{userInfo.city}</h1>
            <h1>{userInfo.state}</h1>
            <h1>{userInfo.safe_email}</h1>
            <h1>{userInfo.email}</h1>
        </div>
    )
}
export default PeopleInfo;
