import React,{useContext} from 'react';
import '../index.css';
import {useParams,Link} from 'react-router-dom';
import { PeopleContext } from '../contexts/PeopleContextProvider';
import {Avatar,Button} from 'evergreen-ui';
import Moment from 'react-moment';
const UserInfo = () => {
    const {_id} = useParams();
    const {data} = useContext(PeopleContext);
    const user = data.find(person=>person.id == _id);
    const dateToFormat = user.logins[user.logins.length-1].date;
    return(
        <div className="user-info-component">
           <div className="personal-info">
           <Avatar name={`${user.first_name} ${user.last_name}`} size={140} />
           <h1 className="user-name">{`${user.first_name} ${user.last_name}`}</h1>
           </div>
           <div id="contact-details">
               <p><span className="prop">City:</span><strong>{user.city}</strong></p>
               <p><span className="prop">State:</span><strong>{user.state}</strong></p>
               <p><span className="prop">Safe email:</span><strong>{user.safe_email}</strong></p>
               <p><span className="prop">Email:</span><strong>{user.email}</strong></p>
               <p><span className="prop">Last activity:</span><strong><Moment>{dateToFormat}</Moment></strong></p>
               <Link to= {`/${_id}/details`}>
                    <Button appearance="primary">Detailed login history</Button>
               </Link>
           </div>
        </div>
    )
}
export default UserInfo;
