import React from 'react';
import {VictoryChart,VictoryLine,VictoryPie} from "victory";
import {Heading} from 'evergreen-ui';
const ActivityGraph = ({stats}) => {
    const {byMonth,byWeek} = stats;
    console.log(stats,'activity')
    return(
        <div className="graphs">
            <Heading size={800}>Monthly activity</Heading>
            <VictoryChart>
                <VictoryLine
                    data={[
                        { x: "Jan", y: byMonth.Jan },
                        { x: "Feb", y: byMonth.Feb },
                        { x: "Mar", y: byMonth.Mar },
                        { x: "Apr", y: byMonth.Apr },
                        { x: "May", y: byMonth.May },
                        { x: "Jun", y: byMonth.Jun },
                        { x: "Jul", y: byMonth.Jul },
                        { x: "Aug", y: byMonth.Aug },
                        { x: "Sep", y: byMonth.Sep },
                        { x: "Oct", y: byMonth.Oct },
                        { x: "Nov", y: byMonth.Nov },
                        { x: "Dec", y: byMonth.Dec }
                        ]}
                />
            </VictoryChart>
            <Heading size={800}>Daily activity</Heading>
            <VictoryPie
                colorScale={["#084B8A", "#00783E", "#00783E", "#EFBB35","#234361","#008f68", "#6DB65B"]}
                data={[
                    { x: "Mon", y: byWeek.Mon },
                    { x: "Tue", y: byWeek.Tue },
                    { x: "Wed", y: byWeek.Wed },
                    { x: "Thu", y: byWeek.Thu },
                    { x: "Fri", y: byWeek.Mon },
                    { x: "Sat", y: byWeek.Sat },
                    { x: "Sun", y: byWeek.Sun }
                    ]}
                    />
        </div>
    )
}
export default ActivityGraph;
