import React,{useContext,setState} from 'react';
import {PeopleContext} from '../contexts/PeopleContextProvider';
import {Table,Button,Pane,Dialog} from 'evergreen-ui'
import PersonInfo from './PersonInfo';

const TableComponent = () => {
const {data} = useContext(PeopleContext);

    return(
        <div className="table-component">
            <Table>
                <Table.SearchHeaderCell  placeholder='Search by email...' height={50}/>
                <Table.Head>
                    <Table.TextHeaderCell>
                        First Name
                    </Table.TextHeaderCell>
                    <Table.TextHeaderCell>
                        Last Name
                    </Table.TextHeaderCell>
                    <Table.TextHeaderCell>
                        City
                    </Table.TextHeaderCell>
                    <Table.TextHeaderCell>
                        State
                    </Table.TextHeaderCell>
                </Table.Head>

                <Table.Body height={600}>
                    {data.map(person => (
                    <Table.Row key={person.id} isSelectable onSelect={() => setState({ isShown: true })}>
                    <PersonInfo initialState={{ isShown: false }}>
                    {({ state, setState }) => (
                      <Pane>
                        <Dialog
                          isShown={state.isShown}
                          title="No footer"
                          onCloseComplete={() => setState({ isShown: false })}
                          hasFooter={false}
                        >
                          No footer
                        </Dialog>
                      </Pane>
                    )}
                  </PersonInfo>}>
                        <Table.TextCell>{person.first_name}</Table.TextCell>
                        <Table.TextCell>{person.last_name}</Table.TextCell>
                        <Table.TextCell>{person.city}</Table.TextCell>
                        <Table.TextCell>{person.state}</Table.TextCell>
                    </Table.Row>
                    ))}
                </Table.Body>
            </Table>
      </div>
    )
}
export default TableComponent;
