import React,{useContext, useState, useEffect} from 'react';
import {PeopleContext} from '../contexts/PeopleContextProvider';
import '../index.css';
import {Table} from 'evergreen-ui'
import {NavLink} from 'react-router-dom';

const TableComponent = () => {
const {data} = useContext(PeopleContext);
const [searchTerm, setSearchTerm] = useState("");
const [searchResults, setSearchResults] = useState([]);
const handleChange = (e) => {
    setSearchTerm(e.target.value)
}
useEffect(()=>{
    const results = data.filter(person=>person.first_name.toLowerCase().includes(searchTerm));
    setSearchResults(results)
},[searchTerm])


    return(
        <div className="table-component">
            <Table>
                <Table.SearchHeaderCell  placeholder='Search by email...' height={50} onChange={handleChange}/>
                <Table.Head>
                    <Table.TextHeaderCell>
                        First Name
                    </Table.TextHeaderCell>
                    <Table.TextHeaderCell>
                        Last Name
                    </Table.TextHeaderCell>
                    <Table.TextHeaderCell>
                        City
                    </Table.TextHeaderCell>
                    <Table.TextHeaderCell>
                        State
                    </Table.TextHeaderCell>
                </Table.Head>

                <Table.Body height={600}>
                    {searchResults.map(person => (
                    <NavLink to={'/' + person.id} key={person.id}>
                        <Table.Row key={person.id}  isSelectable >
                            <Table.TextCell>{person.first_name}</Table.TextCell>
                            <Table.TextCell>{person.last_name}</Table.TextCell>
                            <Table.TextCell>{person.city}</Table.TextCell>
                            <Table.TextCell>{person.state}</Table.TextCell>
                        </Table.Row>
                    </NavLink>
                    ))}
                </Table.Body>
            </Table>
      </div>
    )
}
export default TableComponent;
